import React, { useState, useEffect } from "react";
import axios from "./axios";
import "./Row.css";
import movieTrailer from "movie-trailer";

function Row({ title, imgUrl, bigPoster, description }) {
  const [readMore, setReadMore] = useState(false);

  const handleClick = (movie) => {
    if (trailerUrl) {
      setTrailerUrl("");
    } else {
      movieTrailer(
        movie?.name || movie?.original_name || movie?.original_title || ""
      )
        .then((response) => {
          const urlParams = new URLSearchParams(new URL(response).search);
          setTrailerUrl(urlParams.get("v"));
        })
        .catch((error) => console.log(error));
    }
  };

  return (
    <div className="row">
      <h2>{title}</h2>
      <div className="row__banner">
        <div className="row__bannerOverlay"></div>
        <div className="row__bannerButtons">
          <button className="row__bannerButton">READ MORE</button>
        </div>
      </div>

      <h1 className="row__description">{description}</h1>
    </div>
  );
}

export default Row;
