import React, { useState, useEffect } from "react";
import axios from "./axios";

function Banner({ fetchUrl }) {
  const [banner, setBanner] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(fetchUrl); //IF WE HAVE A VARIABLE LIKE 'fetchUrl', THAT IS OUTSIDE OF BLOCK, WE HAVE DECLARE AT THE END ğŸ‘‡ğŸ»
      setBanner(request.data.results);
      return request;
    }

    fetchData();
  }, [fetchUrl]);

  return (
    <div>
      <h1> Banner </h1>
      <img src={banner[0].backdrop_path} />
    </div>
  );
}

export default Banner;
