import React, { useState, useEffect } from "react";
import axios from "./axios";
import "./Row.css";
import movieTrailer from "movie-trailer";

function Row({ title, imgUrl, bigPoster, description }) {
  const [readMore, setReadMore] = useState(false);

  const handleClick = (status) => {
    if (readMore) {
      setReadMore(false);
    } else {
      setReadMore(true);
    }
  };

  return (
    <div className="row">
      <h2>{title}</h2>
      <div className="row__banner">
        <div className="row__bannerOverlay"></div>
        <div className="row__bannerButtons">
          <button
            className="row__bannerButton"
            onClick={handleClick(!readMore)}
          >
            READ MORE
          </button>
        </div>
      </div>

      {readMore && <h1 className="row__description">{description}</h1>}
    </div>
  );
}

export default Row;
